import { useNavigate } from "react-router-dom";

const SubjectPage = ({ title, description, topics }) => {
  const navigate = useNavigate();

  return (
    <div className="min-h-screen bg-gray-50 pt-24 px-6">
      <div className="max-w-7xl mx-auto text-center">
        <h1 className="text-4xl font-bold text-green-700 mb-4">{title}</h1>
        <p className="text-gray-600 mb-12 text-lg">{description}</p>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {topics.map((topic) => (
            <div
              key={topic.name}
              className={`bg-gradient-to-br ${topic.color} rounded-3xl p-6 shadow-lg hover:shadow-2xl transform hover:-translate-y-1 transition flex flex-col justify-between h-full`}
            >
              <div className="flex flex-col items-center text-center flex-grow space-y-4">
                <div className="bg-white p-3 rounded-full shadow-inner">
                  {topic.icon}
                </div>
                <h2 className="text-2xl font-semibold text-gray-800">{topic.name}</h2>
                <p className="text-gray-600 text-sm flex-grow">{topic.desc}</p>
              </div>
              <button
                onClick={() => navigate(topic.path)}
                className="mt-6 px-5 py-2 bg-green-600 text-white rounded-lg font-medium hover:bg-green-700 transition"
              >
                Start
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default SubjectPage;
