import { useState } from "react";
import { jsPDF } from "jspdf";
import * as pdfjsLib from "pdfjs-dist";
import * as mammoth from "mammoth";

const ResumeAnalyzer = () => {
  const [resumeText, setResumeText] = useState("");
  const [report, setReport] = useState([]);

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    if (file.type === "application/pdf") {
      const fileReader = new FileReader();
      fileReader.onload = async function () {
        const typedArray = new Uint8Array(this.result);
        const pdf = await pdfjsLib.getDocument(typedArray).promise;
        let text = "";
        for (let i = 1; i <= pdf.numPages; i++) {
          const page = await pdf.getPage(i);
          const content = await page.getTextContent();
          text += content.items.map((item) => item.str).join(" ") + "\n";
        }
        setResumeText(text);
      };
      fileReader.readAsArrayBuffer(file);
    } else if (
      file.type ===
        "application/vnd.openxmlformats-officedocument.wordprocessingml.document" ||
      file.name.endsWith(".docx")
    ) {
      const arrayBuffer = await file.arrayBuffer();
      const result = await mammoth.extractRawText({ arrayBuffer });
      setResumeText(result.value);
    } else {
      alert("Only PDF and DOCX files are supported.");
    }
  };

  // ... analysis and download functions remain same

  return (
    <div className="min-h-screen bg-gray-50 pt-24 px-6">
      <div className="max-w-4xl mx-auto text-center">
        <h1 className="text-4xl font-bold text-green-700 mb-6">Resume Analyzer ðŸ“„</h1>

        <input
          type="file"
          accept=".pdf,.docx"
          onChange={handleFileUpload}
          className="mb-4"
        />

        <textarea
          value={resumeText}
          onChange={(e) => setResumeText(e.target.value)}
          rows={10}
          placeholder="Paste your resume here..."
          className="w-full p-4 border rounded-lg mb-6 focus:outline-none focus:ring-2 focus:ring-green-400"
        />

        <button
          onClick={analyzeResume}
          className="bg-green-600 hover:bg-green-700 text-white px-6 py-2 rounded-full shadow-md hover:shadow-lg transition mb-6"
        >
          Analyze Resume
        </button>

        {/* Render report and download button here */}
      </div>
    </div>
  );
};
