// src/components/Navbar.jsx
import { Link, useLocation } from "react-router-dom";

const Navbar = () => {
  const location = useLocation();
  const isLanding = location.pathname === "/";

  return (
    <nav
      className={`${
        isLanding ? "bg-transparent absolute w-full top-0" : "bg-white shadow-md"
      } p-4 flex justify-between items-center z-50`}
    >
      <h1 className={`text-xl font-bold ${isLanding ? "text-white" : "text-blue-600"}`}>
        Placement Platform
      </h1>
      <div className="space-x-4">
        <Link
          to="/"
          className={`${isLanding ? "text-white hover:text-blue-300" : "text-gray-600 hover:text-blue-600"}`}
        >
          Home
        </Link>
        <Link
          to="/login"
          className={`${isLanding ? "text-white hover:text-blue-300" : "text-gray-600 hover:text-blue-600"}`}
        >
          Login
        </Link>
        <Link
          to="/signup"
          className={`${isLanding ? "text-white hover:text-blue-300" : "text-gray-600 hover:text-blue-600"}`}
        >
          Signup
        </Link>
      </div>
    </nav>
  );
};

export default Navbar;
