// src/pages/LandingPage.jsx
const LandingPage = () => {
  return (
    <div
      className="min-h-screen bg-cover bg-center flex flex-col items-center justify-center text-white relative"
      style={{
        backgroundImage:
          "url('https://images.unsplash.com/photo-1605379399642-870262d3d051?auto=format&fit=crop&w=1470&q=80')",
      }}
    >
      {/* Overlay */}
      <div className="absolute inset-0 bg-black bg-opacity-60"></div>

      <div className="relative z-10 text-center px-6">
        <h1 className="text-5xl font-extrabold mb-4">
          Welcome to <span className="text-blue-400">Placement Platform</span>
        </h1>
        <p className="text-lg max-w-2xl mx-auto mb-6">
          Empowering students and recruiters with a seamless campus placement experience.
        </p>
        <div className="space-x-4">
          <a
            href="/login"
            className="px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded-lg font-semibold shadow-lg transition"
          >
            Login
          </a>
          <a
            href="/signup"
            className="px-6 py-3 bg-transparent border border-white hover:bg-white hover:text-blue-600 rounded-lg font-semibold transition"
          >
            Signup
          </a>
        </div>
      </div>
    </div>
  );
};

export default LandingPage;
