import { useState } from "react";
import { useNavigate } from "react-router-dom";

const AIInterviewHome = () => {
  const [file, setFile] = useState(null);
  const navigate = useNavigate();

  const startInterview = async () => {
    if (!file) {
      alert("Please upload resume");
      return;
    }

    const formData = new FormData();
    formData.append("resume", file);
    formData.append("userId", "123"); // later replace with real userId

    const res = await fetch("http://localhost:5000/api/interview/upload-resume", {
      method: "POST",
      body: formData,
    });

    const data = await res.json();

    if (data.sessionId) {
      navigate(`/ai-interview/session/${data.sessionId}`);
    } else {
      alert("Failed to start interview");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white p-8 rounded-xl shadow-lg w-96">
        <h2 className="text-2xl font-bold mb-4 text-center">
          AI Interview Assistant
        </h2>

        <input
          type="file"
          accept=".pdf"
          onChange={(e) => setFile(e.target.files[0])}
          className="mb-4"
        />

        <button
          onClick={startInterview}
          className="w-full bg-green-600 text-white py-2 rounded-lg hover:bg-green-700"
        >
          Start Interview
        </button>
      </div>
    </div>
  );
};

export default AIInterviewHome;
