import { useNavigate } from "react-router-dom";
import { Database, Cpu, Network, Code } from "lucide-react";

const CoreSubjects = () => {
  const navigate = useNavigate();

  const subjects = [
    {
      name: "DBMS",
      desc: "Master concepts like normalization, indexing, SQL queries, and transactions.",
      icon: <Database className="w-10 h-10 text-green-600" />,
      path: "/placement-prep/core-subjects/dbms",
      color: "from-green-100 to-green-50",
    },
    {
      name: "Operating System",
      desc: "Learn about process scheduling, memory management, and deadlocks.",
      icon: <Cpu className="w-10 h-10 text-blue-600" />,
      path: "/placement-prep/core-subjects/os",
      color: "from-blue-100 to-blue-50",
    },
    {
      name: "Computer Networks",
      desc: "Understand TCP/IP, routing, OSI layers, and network security concepts.",
      icon: <Network className="w-10 h-10 text-purple-600" />,
      path: "/placement-prep/core-subjects/cn",
      color: "from-purple-100 to-purple-50",
    },
    {
      name: "Data Structures & Algorithms",
      desc: "Practice problems on arrays, trees, graphs, and algorithmic thinking.",
      icon: <Code className="w-10 h-10 text-pink-600" />,
      path: "/placement-prep/core-subjects/dsa",
      color: "from-pink-100 to-pink-50",
    },
  ];

  return (
    <div className="min-h-screen bg-gray-50 pt-24 px-6">
      <div className="max-w-6xl mx-auto text-center">
        <h1 className="text-4xl font-bold text-green-700 mb-8">
          Core Subjects Preparation
        </h1>
        <p className="text-gray-600 mb-12 text-lg">
          Strengthen your core computer science fundamentals before placements.
        </p>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8">
          {subjects.map((subject) => (
            <div
              key={subject.name}
              className={`bg-gradient-to-br ${subject.color} rounded-3xl p-6 shadow-lg hover:shadow-2xl transform hover:-translate-y-1 transition`}
            >
              <div className="flex flex-col items-center text-center space-y-4">
                <div className="bg-white p-3 rounded-full shadow-inner">
                  {subject.icon}
                </div>
                <h2 className="text-2xl font-semibold text-gray-800">
                  {subject.name}
                </h2>
                <p className="text-gray-600 text-sm">{subject.desc}</p>
                <button
                  onClick={() => navigate(subject.path)}
                  className="mt-4 px-5 py-2 bg-green-600 text-white rounded-lg font-medium hover:bg-green-700 transition"
                >
                  Start
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CoreSubjects;
