import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { BookOpen } from "lucide-react"; // optional icon

const PlacementPrep = () => {
  const navigate = useNavigate();
  const [modules, setModules] = useState([
    {
      name: "Core Subjects",
      desc: "DBMS, OS, CN, DSA topics for placements",
      path: "/placement-prep/core-subjects",
    },
    {
      name: "Aptitude",
      desc: "Quantitative, Logical, and Verbal ability questions",
      path: "/placement-prep/aptitude",
    },
    {
      name: "Resume Analyzer",
      desc: "AI-powered resume feedback & optimization",
      path: "/placement-prep/resume-analyzer",
    },
  ]);

  return (
    <div className="min-h-screen bg-gray-50 px-6 py-24">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-4xl font-bold text-green-700 mb-6 text-center">
          Placement Preparation
        </h1>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {modules.map((m) => (
            <div
              key={m.name}
              onClick={() => navigate(m.path)}
              className="relative rounded-2xl overflow-hidden shadow-lg transform transition hover:scale-105 cursor-pointer group bg-white p-6"
            >
              <div className="flex items-center gap-3 mb-3">
                <BookOpen className="w-10 h-10 text-green-600" />
                <h2 className="text-2xl font-semibold">{m.name}</h2>
              </div>
              <p className="text-gray-600">{m.desc}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default PlacementPrep;
