import { useNavigate } from "react-router-dom";
import { BookOpen } from "lucide-react";

const coreSubjects = () => {
  const navigate = useNavigate();

  const topics = [
    {
      name: "DBMS",
      desc: "Database concepts, normalization, SQL queries, transactions.",
      path: "/placement-prep/core-subjects/dbms",
      color: "bg-green-600",
    },
    {
      name: "Operating System",
      desc: "Processes, threads, scheduling, memory management.",
      path: "/placement-prep/core-subjects/os",
      color: "bg-blue-600",
    },
    {
      name: "Computer Networks",
      desc: "OSI, TCP/IP, protocols, routing, and switching.",
      path: "/placement-prep/core-subjects/cn",
      color: "bg-purple-600",
    },
    {
      name: "DSA",
      desc: "Data structures, algorithms, problem solving techniques.",
      path: "/placement-prep/core-subjects/dsa",
      color: "bg-pink-600",
    },
  ];

  return (
    <div className="min-h-screen bg-gray-50 px-6 py-24">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-4xl font-bold text-green-700 mb-6 text-center">
          Core Subjects
        </h1>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
          {topics.map((topic) => (
            <div
              key={topic.name}
              onClick={() => navigate(topic.path)}
              className={`rounded-2xl overflow-hidden shadow-lg transform transition hover:scale-105 cursor-pointer group ${topic.color} text-white p-6`}
            >
              <div className="flex items-center gap-3 mb-3">
                <BookOpen className="w-10 h-10 text-white" />
                <h2 className="text-2xl font-semibold">{topic.name}</h2>
              </div>
              <p className="text-white/90">{topic.desc}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default coreSubjects;
