import React from "react";
import { useNavigate } from "react-router-dom";

const CoreSubjects = () => {
  const navigate = useNavigate();

  const subjects = [
    {
      name: "DBMS",
      description:
        "Learn about database concepts, normalization, SQL queries, indexing, and transactions.",
      route: "/placement-prep/core-subjects/dbms",
    },
    {
      name: "Operating System",
      description:
        "Understand OS concepts like process management, deadlocks, memory allocation, and scheduling.",
      route: "/placement-prep/core-subjects/os",
    },
    {
      name: "Computer Networks",
      description:
        "Study networking layers, protocols (TCP/IP), routing algorithms, and network security.",
      route: "/placement-prep/core-subjects/cn",
    },
    {
      name: "DSA",
      description:
        "Master data structures and algorithms for coding interviews, problem-solving, and optimization.",
      route: "/placement-prep/core-subjects/dsa",
    },
    {
      name: "Software Engineering",
      description:
        "Explore SDLC, design patterns, agile methods, testing, and software maintenance.",
      route: "/placement-prep/core-subjects/software-engineering",
    },
    {
      name: "OOPs",
      description:
        "Understand core OOP concepts: encapsulation, inheritance, polymorphism, and abstraction.",
      route: "/placement-prep/core-subjects/oops",
    },
    {
      name: "System Design",
      description:
        "Learn the fundamentals of designing scalable, reliable, and maintainable systems.",
      route: "/placement-prep/core-subjects/system-design",
    },
    {
      name: "Computer Fundamentals",
      description:
        "Revise computer basics — logic gates, memory units, microprocessors, and architecture.",
      route: "/placement-prep/core-subjects/computer-fundamentals",
    },
  ];

  return (
    <div className="pt-24 px-6 min-h-screen bg-gradient-to-b from-gray-50 to-gray-100">
      <h2 className="text-3xl font-bold text-center mb-10 text-gray-800">
        Core Subjects
      </h2>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
        {subjects.map((subject, index) => (
          <div
            key={index}
            className="bg-white p-6 rounded-2xl shadow-md flex flex-col justify-between transition-all duration-300 hover:-translate-y-2 hover:shadow-2xl hover:border-blue-200 border border-transparent"
          >
            <div>
              <h3 className="text-xl font-semibold mb-2 text-gray-900">
                {subject.name}
              </h3>
              <p className="text-gray-600 text-sm">{subject.description}</p>
            </div>

            <div className="mt-6 flex justify-start">
              <button
                onClick={() => navigate(subject.route)}
                className="px-4 py-2 text-sm bg-blue-600 text-white rounded-lg hover:shadow-[0_0_12px_2px_rgba(37,99,235,0.4)] transition-all duration-300"
              >
                Start
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CoreSubjects;
