// frontend/src/components/Navbar.jsx
import React from "react";
import { Link, useLocation } from "react-router-dom";

const Navbar = () => {
  const location = useLocation();
  const isLanding = location.pathname === "/";

  return (
    <nav
      className={`w-full z-50 ${
        isLanding ? "absolute top-0 left-0" : "relative"
      }`}
      style={{ height: "64px" }} // consistent height
    >
      <div
        className={`max-w-7xl mx-auto px-6 flex items-center justify-between h-full ${
          isLanding ? "" : "bg-white shadow-sm"
        }`}
      >
        <Link
          to="/"
          className={`text-2xl font-bold transition ${
            isLanding ? "text-white" : "text-green-600"
          }`}
        >
          Placement Platform
        </Link>

        <div className="space-x-6 flex items-center">
          <Link
            to="/"
            className={`text-lg font-medium transition ${
              isLanding ? "text-white hover:text-green-200" : "text-gray-700 hover:text-green-500"
            }`}
          >
            Home
          </Link>

          <Link
            to="/login"
            className={`text-lg font-medium transition ${
              isLanding ? "text-white hover:text-green-200" : "text-gray-700 hover:text-green-500"
            }`}
          >
            Login
          </Link>

          <Link
            to="/signup"
            className={`px-4 py-2 rounded-lg font-semibold transition ${
              isLanding
                ? "border border-white text-white hover:bg-white hover:text-green-600"
                : "bg-green-600 text-white hover:bg-green-700"
            }`}
          >
            Signup
          </Link>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
