import React, { useEffect, useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";

const Navbar = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) setUser(JSON.parse(storedUser));
    else setUser(null);
  }, [location]); // re-run on location change

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  return (
    <nav className="fixed top-0 left-0 w-full bg-transparent backdrop-blur-md z-50 shadow-sm">
      <div className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
        {/* Logo */}
        <Link
          to="/"
          className="text-2xl font-bold text-green-600 hover:text-green-700 transition"
        >
          Career Bridge
        </Link>

        {/* Navigation Links */}
        <div className="space-x-6">
          <Link
            to="/"
            className={`text-lg font-medium transition ${
              location.pathname === "/" ? "text-green-600" : "text-white hover:text-green-500"
            }`}
          >
            Home
          </Link>

          {user ? (
            <>
              <Link
                to="/student/dashboard"
                className="text-lg font-medium text-white hover:text-green-500 transition"
              >
                Dashboard
              </Link>
              <button
                onClick={handleLogout}
                className="text-lg font-medium text-red-500 hover:text-red-700 transition"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <Link
                to="/login"
                className={`text-lg font-medium transition ${
                  location.pathname === "/login"
                    ? "text-green-600"
                    : "text-white hover:text-green-500"
                }`}
              >
                Login
              </Link>
              <Link
                to="/signup"
                className={`text-lg font-medium transition ${
                  location.pathname === "/signup"
                    ? "text-green-600"
                    : "text-white hover:text-green-500"
                }`}
              >
                Signup
              </Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
