import React from "react";

const LandingPage = () => {
  return (
    <div className="relative min-h-screen flex items-center justify-center overflow-hidden">
      {/* ✅ Background Image */}
      <div
        className="absolute inset-0 bg-cover bg-center"
        style={{
          backgroundImage:
            "url('https://images.unsplash.com/photo-1510519138101-570d1dca3d66?ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&q=80&w=1147')",
        }}
      ></div>

      {/* ✅ Softer Gradient Overlay (lets image show through) */}
      <div className="absolute inset-0 bg-gradient-to-br from-green-200 via-blue-100 to-purple-200 opacity-70 backdrop-blur-[1px]"></div>

      {/* ✅ Content */}
      <div className="relative z-10 text-center px-6">
        <h1 className="text-5xl font-extrabold text-green-700 mb-4 drop-shadow-lg">
          Welcome to Placement Platform
        </h1>
        <p className="text-gray-700 text-lg max-w-2xl mx-auto mb-6">
          A unified platform connecting students and recruiters for a smarter,
          faster, and more transparent placement process.
        </p>
        <div className="space-x-4">
          <a
            href="/login"
            className="px-6 py-3 bg-green-600 hover:bg-green-700 text-white rounded-lg font-semibold shadow-lg transition"
          >
            Login
          </a>
          <a
            href="/signup"
            className="px-6 py-3 bg-transparent border border-green-600 text-green-700 hover:bg-green-600 hover:text-white rounded-lg font-semibold transition"
          >
            Signup
          </a>
        </div>
      </div>
    </div>
  );
};

export default LandingPage;
