import React from "react";
import { Link, useLocation } from "react-router-dom";

const Navbar = () => {
  const location = useLocation();

  // Check if user is logged in
  const isLoggedIn = !!localStorage.getItem("token");

  const linkClasses = (path) =>
    `text-lg font-medium transition ${
      location.pathname === path
        ? "text-green-700"
        : isLoggedIn
        ? "text-green-600 hover:text-green-700"
        : "text-white hover:text-green-500"
    }`;

  const navbarBg = isLoggedIn ? "bg-white shadow-md" : "bg-transparent backdrop-blur-md";

  return (
    <nav className={`fixed top-0 left-0 w-full z-50 ${navbarBg}`}>
      <div className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
        {/* Logo / Brand */}
        <Link
          to="/"
          className={`text-2xl font-bold transition ${
            isLoggedIn ? "text-green-700 hover:text-green-800" : "text-green-600 hover:text-green-700"
          }`}
        >
          Career Bridge
        </Link>

        {/* Navigation Links */}
        <div className="space-x-6">
          {!isLoggedIn ? (
            <>
              <Link to="/" className={linkClasses("/")}>
                Home
              </Link>
              <Link to="/about" className={linkClasses("/about")}>
                About
              </Link>
              <Link to="/contact" className={linkClasses("/contact")}>
                Contact
              </Link>
              <Link to="/login" className={linkClasses("/login")}>
                Login
              </Link>
              <Link to="/signup" className={linkClasses("/signup")}>
                Signup
              </Link>
            </>
          ) : (
            <>
              <Link to="/student/dashboard" className={linkClasses("/student/dashboard")}>
                Dashboard
              </Link>
              <button
                onClick={() => {
                  localStorage.removeItem("token");
                  localStorage.removeItem("user");
                  window.location.href = "/login";
                }}
                className="text-green-600 hover:text-green-700 font-medium transition"
              >
                Logout
              </button>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
