import { useNavigate } from "react-router-dom";
import {
  Calculator,
  Brain,
  MessageSquare,
  BarChart3,
} from "lucide-react";

const Aptitude = () => {
  const navigate = useNavigate();

  const topics = [
    {
      name: "Quantitative Aptitude",
      desc: "Covers arithmetic, algebra, geometry, percentages, and data interpretation.",
      icon: <Calculator className="w-10 h-10 text-green-600" />,
      path: "/placement-prep/aptitude/quantitative",
      color: "from-green-100 to-green-50",
    },
    {
      name: "Logical Reasoning",
      desc: "Practice puzzles, patterns, seating arrangements, and analytical reasoning questions.",
      icon: <Brain className="w-10 h-10 text-blue-600" />,
      path: "/placement-prep/aptitude/logical",
      color: "from-blue-100 to-blue-50",
    },
    {
      name: "Verbal Ability",
      desc: "Enhance grammar, comprehension, sentence correction, and vocabulary skills.",
      icon: <MessageSquare className="w-10 h-10 text-purple-600" />,
      path: "/placement-prep/aptitude/verbal",
      color: "from-purple-100 to-purple-50",
    },
    {
      name: "Data Interpretation",
      desc: "Learn to analyze charts, graphs, and tables to extract meaningful insights quickly.",
      icon: <BarChart3 className="w-10 h-10 text-pink-600" />,
      path: "/placement-prep/aptitude/data-interpretation",
      color: "from-pink-100 to-pink-50",
    },
  ];

  return (
    <div className="min-h-screen bg-gray-50 pt-24 px-6">
      <div className="max-w-7xl mx-auto text-center">
        <h1 className="text-4xl font-bold text-blue-700 mb-8">
          Aptitude Preparation
        </h1>
        <p className="text-gray-600 mb-12 text-lg">
          Sharpen your quantitative, logical, and verbal reasoning skills to
          excel in placement exams.
        </p>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8">
          {topics.map((topic) => (
            <div
              key={topic.name}
              className={`bg-gradient-to-br ${topic.color} rounded-3xl p-6 shadow-lg hover:shadow-2xl transform hover:-translate-y-1 transition flex flex-col justify-between h-full`}
            >
              <div className="flex flex-col items-center text-center flex-grow space-y-4">
                <div className="bg-white p-3 rounded-full shadow-inner">
                  {topic.icon}
                </div>
                <h2 className="text-2xl font-semibold text-gray-800">
                  {topic.name}
                </h2>
                <p className="text-gray-600 text-sm flex-grow">{topic.desc}</p>
              </div>
              <button
                onClick={() => navigate(topic.path)}
                className="mt-6 px-5 py-2 bg-blue-600 text-white rounded-lg font-medium hover:bg-blue-700 transition"
              >
                Start
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Aptitude;
