import { useState } from "react";
import { jsPDF } from "jspdf";

const ResumeAnalyzer = () => {
  const [resumeText, setResumeText] = useState("");
  const [report, setReport] = useState([]);

  const analyzeResume = () => {
    // Minimal example of actionable analysis
    const missingSections = [];
    if (!resumeText.includes("Experience")) missingSections.push("Experience section missing");
    if (!resumeText.includes("Projects")) missingSections.push("Add Projects section");
    if (!resumeText.includes("Skills")) missingSections.push("Skills section missing");
    if (!resumeText.includes("Education")) missingSections.push("Education section missing");

    const suggestions = [];
    if (resumeText.length > 2000) suggestions.push("Summary or descriptions too long, make it concise");
    if (resumeText.length < 300) suggestions.push("Resume seems too short, add more details");

    const finalReport = [
      { title: "Missing Sections", items: missingSections, color: "from-red-100 to-red-50" },
      { title: "Content Suggestions", items: suggestions, color: "from-yellow-100 to-yellow-50" },
    ];

    setReport(finalReport);
  };

  const downloadPDF = () => {
    const doc = new jsPDF();
    doc.setFontSize(16);
    doc.text("Resume Analysis Report", 10, 10);
    let y = 20;

    report.forEach((section) => {
      doc.setFontSize(14);
      doc.text(section.title, 10, y);
      y += 8;
      section.items.forEach((item) => {
        doc.setFontSize(12);
        doc.text("- " + item, 15, y);
        y += 7;
      });
      y += 5;
    });

    doc.save("Resume_Report.pdf");
  };

  return (
    <div className="min-h-screen bg-gray-50 pt-24 px-6">
      <div className="max-w-4xl mx-auto text-center">
        <h1 className="text-4xl font-bold text-green-700 mb-6">Resume Analyzer ðŸ“„</h1>
        <p className="text-gray-600 mb-6">
          Paste your resume below to get actionable feedback and suggestions.
        </p>

        <textarea
          value={resumeText}
          onChange={(e) => setResumeText(e.target.value)}
          rows={10}
          placeholder="Paste your resume here..."
          className="w-full p-4 border rounded-lg mb-6 focus:outline-none focus:ring-2 focus:ring-green-400"
        />

        <button
          onClick={analyzeResume}
          className="bg-green-600 hover:bg-green-700 text-white px-6 py-2 rounded-full shadow-md hover:shadow-lg transition mb-6"
        >
          Analyze Resume
        </button>

        {report.length > 0 && (
          <div className="mt-8 grid grid-cols-1 sm:grid-cols-2 gap-6">
            {report.map((section) => (
              <div
                key={section.title}
                className={`bg-gradient-to-br ${section.color} p-6 rounded-3xl shadow-lg flex flex-col`}
              >
                <h2 className="text-2xl font-semibold text-gray-800 mb-4">{section.title}</h2>
                <ul className="text-gray-700 text-left list-disc list-inside">
                  {section.items.length > 0
                    ? section.items.map((item, idx) => <li key={idx}>{item}</li>)
                    : <li>All good âœ…</li>}
                </ul>
              </div>
            ))}
          </div>
        )}

        {report.length > 0 && (
          <button
            onClick={downloadPDF}
            className="mt-6 bg-green-600 hover:bg-green-700 text-white px-6 py-2 rounded-full shadow-md hover:shadow-lg transition"
          >
            Download Report
          </button>
        )}
      </div>
    </div>
  );
};

export default ResumeAnalyzer;
